import 'package:firebase_auth/firebase_auth.dart';import 'package:firebase_core/firebase_core.dart';import 'package:cloud_firestore/cloud_firestore.dart';import 'package:flutter/material.dart';import 'package:google_mobile_ads/google_mobile_ads.dart';import 'package:sovo_test_app/pages/homeView.dart';import 'package:sovo_test_app/pages/loginView.dart';import 'package:sovo_test_app/pages/registerView.dart';import 'package:sovo_test_app/widgets/snackBars.dart';import 'firebase_options.dart';Future<void> main() async {  WidgetsFlutterBinding.ensureInitialized();  await Firebase.initializeApp(options: DefaultFirebaseOptions.android);  // Admob.initialize();  runApp(const MyApp());}class MyApp extends StatefulWidget {  const MyApp({Key? key}) : super(key: key);  @override  State<MyApp> createState() => _MyAppState();}class _MyAppState extends State<MyApp> {  @override  Widget build(BuildContext context) {    return MaterialApp(      home: const MyHomePage(),      theme: ThemeData(        appBarTheme: AppBarTheme(          color: Colors.black12,          elevation: 15,        ),        floatingActionButtonTheme: FloatingActionButtonThemeData(          backgroundColor: Colors.lightGreenAccent.shade400,        ),      ),    );  }}class MyHomePage extends StatefulWidget {  const MyHomePage({Key? key}) : super(key: key);  @override  State<MyHomePage> createState() => _MyHomePageState();}class _MyHomePageState extends State<MyHomePage> {  bool userSignedIn = false;  bool showRegistration = false;  FirebaseAuth auth = FirebaseAuth.instance;  void isUserSignedIn() {    FirebaseAuth.instance.authStateChanges().listen((User? user) {      print(user);      if (user == null) {        userSignedIn = false;      } else {        userSignedIn = true;      }    });  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: FutureBuilder(          future: Firebase.initializeApp(),          builder: (context, snapshot) {            if (userSignedIn == false) {              return LoginView();            } else {              return HomeView();            }          }),    );  }  @override  void initState() {    isUserSignedIn();  }}